export interface IMonsterSkillElement extends IElementBase{
 	/**id*/
	ID:number
	/**技能名*/
	skill_name:string
	/**技能预告文字*/
	predictDesc:string
	/**冷却（s）*/
	cd:number
	/**持续时间*/
	duration:number
	/**前摇时间(s)*/
	preTime:number
	/**是否技能预警*/
	isPredict:number
	/**攻击类型*/
	effectType:number
	/**技能特效物件*/
	envirEffItem:string
	/**预警圈区域中心点坐标*/
	envirRelesePos:mw.Vector
	/**受击延迟*/
	onHitDelay:number
	/**释放特效延迟*/
	bulletFly:number
	/**子弹飞行速度*/
	bulletSpeed:number
	/**时间为特效播放时间（弹道速度）*/
	starget:number
	/**目标类型*/
	scope:number
	/**技能释放判断范围（追击距离）*/
	radius:number
	/**攻击范围半径*/
	radiusStart:number
	/**释放起点距离*/
	buff:Array<number>
	/**增减益（buff表ID）*/
	angle:Array<number>
	/**释放角度*/
	shape:number
	/**攻击形状（近战）*/
	hurt_angle:number
	/**攻击角度*/
	hurtOffset:Array<number>
	/**近战中心点偏移*/
	rectangleValue:Array<number>
	/**矩形参数(宽高)*/
	displacementStarget:number
	/**位移改变目标*/
	displacement:Array<number>
	/**位移角度*/
	damage:Array<number>
	/**伤害*/
	damageRate:number
	/**伤害倍率(百分比)*/
	release_sound:string
	/**释放音效*/
	releaseSoundCount:number
	/**播放次数*/
	releaseSoundVolume:number
	/**释放音效的音量比例(0-1)*/
	release_effect:string
	/**释放特效（effet表ID）*/
	hitEffectGuid:string
	/**命中时的特效（effectID）*/
	hitSoundGuid:string
	/**命中音效(3d)*/
	hitSoundVolume:number
	/**1次性的（Guid)*/
	first_type:number
	/**命中音效的音量比例(0-1)*/
	first_value:number
 } 
